$outputFile = "CurveRightEndDataIndex.asm"

# Clear or create file with header comment
" ; CurveRightEndDataIndex generated by script" | Out-File -Encoding ascii $outputFile

for ($i = 75; $i -ge 1; $i--) {
    $j = $i - 1
    foreach ($page in 0, 1) {
        foreach ($region in "TOP", "BOTTOM") {
            $block = "BlockCurveRightEndPage${page}${region}from${i}to${j}"
            $changed = "CurveRightEndPage${page}${region}ChangedPixelsfrom${i}to${j}"
            $addr = "CurveRightEndPage${page}${region}Addressesfrom${i}to${j}"
            $instr = "CurveRightEndPage${page}${region}WriteInstructionsfrom${i}to${j}"
            # Final flag formula
            if ($region -eq "TOP") {
                $flag = $page * 2
            } else {
                $flag = $page * 2 + 1
            }

            $line = "db $block, $flag | dw $changed, $addr, $instr"
            Add-Content -Path $outputFile -Encoding ascii -Value $line
        }
    }
}
